<style lang="scss">
	.sidebar__type {
		padding: 0.76rem;
		padding-bottom: 0.5rem;
	}

	[type='radio'] {
		display: none;
	}

	label {
		display: block;
		margin-bottom: 0;

		&:not(:last-child) {
			margin-bottom: 0.4rem;
		}

		& svg {
			width: 3.5rem;
		}
	}

	[type='radio']:checked + label svg {
		filter: drop-shadow(0 0 0.6rem rgba(255, 138, 54, 0.5));
	}
	svg {
		fill-opacity: 0.05;
	}
	.bg {
		fill: #ff8a36;
		fill-opacity: 0;
		transition: fill-opacity 0.6s;
	}
	.icon {
		opacity: 0.3;
	}
	[type='radio']:checked + label .bg {
		fill-opacity: 1;
	}
	[type='radio']:checked + label .icon {
		opacity: 1;
	}
</style>
